SELECT a.bs, a.b, a.c
FROM (
    SELECT
    'Bagaimana komunikasi kami di dalam pelaksanaan proyek ini.' AS parameter_penilaian,
    (
        SELECT COUNT(*) FROM `tabKUESIONER LAPANGAN _ SOAL` WHERE pertanyaan = 'Bagaimana komunikasi kami di dalam pelaksanaan proyek ini.' AND jawaban = 'Baik Sekali'
    ) AS bs,
    (
        SELECT COUNT(*) FROM `tabKUESIONER LAPANGAN _ SOAL` WHERE pertanyaan = 'Bagaimana komunikasi kami di dalam pelaksanaan proyek ini.' AND jawaban = 'Baik'
    ) AS b,
    (
        SELECT COUNT(*) FROM `tabKUESIONER LAPANGAN _ SOAL` WHERE pertanyaan = 'Bagaimana komunikasi kami di dalam pelaksanaan proyek ini.' AND jawaban = 'Cukup'
    ) AS c,
    (
        SELECT COUNT(*) FROM `tabKUESIONER LAPANGAN _ SOAL` WHERE pertanyaan = 'Bagaimana komunikasi kami di dalam pelaksanaan proyek ini.' AND jawaban = 'Kurang'
    ) AS k,
    (
        SELECT COUNT(*) FROM `tabKUESIONER LAPANGAN _ SOAL` WHERE pertanyaan = 'Bagaimana komunikasi kami di dalam pelaksanaan proyek ini.' AND jawaban = 'Sangat Kurang'
    ) AS sk
) a
